    "mode": "Indexed",
    "description": "Include this rule to deploy the VM extensions for Microsoft Guest Configuration, Microsoft Azure Managed Service Identity, and required content to check settings inside the virtual machine. For Linux servers this includes Chef Inspec, Ruby, and Python. This security setting verifies there are no accounts without passwords.",
    "metadata": {
      "category": "Guest Configuration"
    },
    "policyRule": {
      "if": {
        "allOf": [
          {
            "field": "type",
            "equals": "Microsoft.Compute/virtualMachines"
          },
          {
            "anyOf": [
              {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "equals": "Canonical"
                  },
                  {
                    "field": "Microsoft.Compute/imageOffer",
                    "equals": "UbuntuServer"
                  },
                  {
                    "anyOf": [
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "match": "14.04.#-LTS"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "match": "16.04.#-LTS"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "equals": "16.04-LTS"
                      },
                      {
                        "field": "Microsoft.Compute/imageSKU",
                        "match": "18.04-LTS"
                      }
                    ]
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "equals": "Suse"
                  },
                  {
                    "field": "Microsoft.Compute/imageOffer",
                    "equals": "SLES"
                  },
                  {
                    "field": "Microsoft.Compute/imageSKU",
                    "equals": "12-SP3"
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "field": "Microsoft.Compute/imagePublisher",
                    "equals": "RedHat"
                  },
                  {
                    "field": "Microsoft.Compute/imageOffer",
                    "equals": "RHEL"
                  },
                  {
                    "field": "Microsoft.Compute/imageSKU",
                    "in": [
                      "7-RAW",
